import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import CourseView from './CourseView'; // Import the CourseView component

function ScheduleView() {
  const location = useLocation();
  const { studentId, firstName, lastName } = location.state;
  const [schedule, setSchedule] = useState([]);
  const [error, setError] = useState(null);
  const [selectedCourse, setSelectedCourse] = useState(null); // For modal control

  useEffect(() => {
    const fetchSchedule = async () => {
      try {
        const response = await axios.get(`/api/schedule/${studentId}`);
        setSchedule(response.data);
      } catch (err) {
        setError('Failed to load schedule');
      }
    };
    fetchSchedule();
  }, [studentId]);

  const openCourseDetails = async (courseId) => {
    try {
      const response = await axios.get(`/api/course/${courseId}`);
      setSelectedCourse(response.data); // Set course data for CourseView modal
    } catch (err) {
      console.error("Error fetching course details:", err);
      setError('Failed to load course details');
    }
  };

  const closeModal = () => setSelectedCourse(null);

  return (
    <div>
      <h2>Welcome, {firstName} {lastName} - Fall 2024</h2>
      {error ? (
        <p style={{ color: 'red' }}>{error}</p>
      ) : (
        <ul>
          {schedule.map((course) => (
            <li key={course.course_id}>
              <strong>Course Number:</strong> {course.course_number}<br />
              <strong>Course Name:</strong> {course.course_name}<br />
              <button onClick={() => openCourseDetails(course.course_id)}>
                View Course Details
              </button>
            </li>
          ))}
        </ul>
      )}
      {selectedCourse && (
        <CourseView course={selectedCourse} onClose={closeModal} />
      )}
    </div>
  );
}

export default ScheduleView;
