import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

function ScheduleView() {
  const location = useLocation();
  const navigate = useNavigate();
  const { state } = location;
  const studentId = state ? state.studentId : null;
  const firstName = state ? state.firstName : '';
  const lastName = state ? state.lastName : '';

  const [schedule, setSchedule] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSchedule = async () => {
      if (studentId) {
        try {
          const response = await axios.get(`/api/schedule/${studentId}`);
          setSchedule(response.data);
        } catch (err) {
          setError('Failed to load schedule');
        }
      } else {
        setError('No student information available');
      }
    };
    fetchSchedule();
  }, [studentId]);

  const viewCourseDetails = (course) => {
    // Navigate to CourseView and pass the selected course data
    navigate('/course', { state: { course } });
  };

  return (
    <div>
      <h2>Welcome, {firstName} {lastName} - Fall 2024</h2>
      {error ? (
        <p style={{ color: 'red' }}>{error}</p>
      ) : (
        <ul>
          {schedule.map((course) => (
            <li key={course.course_id}>
              <strong>Course Number:</strong> {course.course_number}<br />
              <strong>Course Name:</strong> {course.course_name}<br />
              {/* Button to view course details */}
              <button onClick={() => viewCourseDetails(course)}>
                View Course Details
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default ScheduleView;
